create table SALE_DATA (DATE_OPERATION date not null, SERVER_ID integer not null, AMOUNT_10_TAXES double, AMOUNT_20_TAXES double, IS_LOCKED boolean, NB_COUVERT integer, primary key (DATE_OPERATION, SERVER_ID));
