create table SERVER (ID integer generated by default as identity, FIRST_NAME varchar(255), LAST_NAME varchar(255), primary key (ID));
create table USER (ID integer generated by default as identity, NAME varchar(255), PASSWORD varchar(255), primary key (ID));
create table USER_ROLE (USER_ID integer not null, ROLE_ID integer not null);
